graphql.query:
query customerGroups {
    customerGroups {
        paginatorInfo {
            count
            currentPage
            lastPage
            total
        }
        data {
            id
            name
            code
            isUserDefined
            createdAt
            updatedAt
        }
    }
}

query customerGroup {
    customerGroup(id: 1) {
        id
        name
        code
        isUserDefined
        createdAt
        updatedAt
    }
}

mutation createCustomerGroup {
    createCustomerGroup(input: {
        name: "VIP Group"
        code: "vip"
        isUserDefined: false
    }) {
        id
        name
        code
        isUserDefined
        createdAt
        updatedAt
    }
}

mutation updateCustomerGroup {
    updateCustomerGroup(id: 4, input: {
        name: "VIP Customers"
        code: "vip"
        isUserDefined: true
    }) {
        id
        name
        code
        isUserDefined
        createdAt
        updatedAt
    }
}

